#!/usr/bin/env node

/**
 * Simple MCP Server for Content Generation
 * Implements getTinyImage, echo, and addNumbers tools locally
 */

import { Server } from '@modelcontextprotocol/sdk/server/index.js';
import { StdioServerTransport } from '@modelcontextprotocol/sdk/server/stdio.js';
import {
  CallToolRequestSchema,
  ErrorCode,
  ListToolsRequestSchema,
  McpError,
} from '@modelcontextprotocol/sdk/types.js';

// Tiny 16x16 PNG image in base64 (a simple colored square)
const TINY_IMAGE_BASE64 = '';
class SimpleMCPServer {
  constructor() {
    this.server = new Server(
      {
        name: 'simple-content-server',
        version: '1.0.0',
      },
      {
        capabilities: {
          tools: {},
        },
      }
    );

    this.setupToolHandlers();
    this.server.setRequestHandler(ListToolsRequestSchema, this.handleListTools.bind(this));
    this.server.setRequestHandler(CallToolRequestSchema, this.handleCallTool.bind(this));
  }

  setupToolHandlers() {
    // Tool handlers are set up in handleCallTool
  }

  async handleListTools() {
    return {
      tools: [
        {
          name: 'getTinyImage',
          description: 'Generate a tiny 16x16 pixel sample image',
          inputSchema: {
            type: 'object',
            properties: {},
            required: [],
          },
        },
        {
          name: 'echo',
          description: 'Echo back text with optional modifications',
          inputSchema: {
            type: 'object',
            properties: {
              text: {
                type: 'string',
                description: 'The text to echo back',
              },
              modifier: {
                type: 'string',
                description: 'Optional modifier to apply to the text',
                enum: ['uppercase', 'lowercase', 'reverse'],
              },
            },
            required: ['text'],
          },
        },
        {
          name: 'addNumbers',
          description: 'Add two numbers together',
          inputSchema: {
            type: 'object',
            properties: {
              a: {
                type: 'number',
                description: 'First number',
              },
              b: {
                type: 'number',
                description: 'Second number',
              },
            },
            required: ['a', 'b'],
          },
        },
      ],
    };
  }

  async handleCallTool(request) {
    const { name, arguments: args } = request.params;

    switch (name) {
      case 'getTinyImage':
        return {
          content: [
            {
              type: 'text',
              text: 'This is a tiny image:',
            },
            {
              type: 'image',
              data: TINY_IMAGE_BASE64,
              mimeType: 'image/png',
            },
            {
              type: 'text',
              text: 'The image above is the MCP tiny image.',
            },
          ],
        };

      case 'echo':
        let result = args.text;
        if (args.modifier) {
          switch (args.modifier) {
            case 'uppercase':
              result = result.toUpperCase();
              break;
            case 'lowercase':
              result = result.toLowerCase();
              break;
            case 'reverse':
              result = result.split('').reverse().join('');
              break;
          }
        }
        return {
          content: [
            {
              type: 'text',
              text: `Echo: ${result}`,
            },
          ],
        };

      case 'addNumbers':
        const sum = args.a + args.b;
        return {
          content: [
            {
              type: 'text',
              text: `${args.a} + ${args.b} = ${sum}`,
            },
          ],
        };

      default:
        throw new McpError(
          ErrorCode.MethodNotFound,
          `Unknown tool: ${name}`
        );
    }
  }

  async run() {
    const transport = new StdioServerTransport();
    await this.server.connect(transport);
    console.error('Simple MCP Content Server running on stdio');
  }
}

// Run the server
const server = new SimpleMCPServer();
server.run().catch(console.error);
// Keep the process alive when running the server manually so it doesn't exit immediately.
// When spawned by the agent as a child process with stdio pipes, this isn't necessary,
// but it helps when debugging by running `node simple_mcp_server.js` directly.
process.stdin.resume();